(1817, 177, 'services_details_repeater_0_service_details_content', 'Black walnut is rich, dark, and dense with a beautiful grain pattern. It is prized for its durability, elegance, and is often used for fine furniture and cabinetry.'),
(1818, 177, '_services_details_repeater_0_service_details_content', 'field_64f6e4f329d8b'),
(1819, 177, 'services_details_repeater_1_service_details_title', 'Ash'),
(1820, 177, '_services_details_repeater_1_service_details_title', 'field_64f6e4dd29d8a'),
(1821, 177, 'services_details_repeater_1_service_details_content', 'Ash is pale, with a straight grain and prominent growth rings. It is tough, resilient, and used for furniture, flooring, and cabinets, and more.'),
(1822, 177, '_services_details_repeater_1_service_details_content', 'field_64f6e4f329d8b'),
