(1824, 177, '_services_details_repeater_2_service_details_title', 'field_64f6e4dd29d8a'),
(1825, 177, 'services_details_repeater_2_service_details_content', 'Oak is renowned for its strong, dense nature and prominent grain patterns. It exudes a warm, timeless beauty, making it a popular choice for furniture, flooring, and more.'),
(1826, 177, '_services_details_repeater_2_service_details_content', 'field_64f6e4f329d8b'),
