(1828, 177, '_services_details_repeater_3_service_details_title', 'field_64f6e4dd29d8a'),
(1829, 177, 'services_details_repeater_3_service_details_content', 'Elm is characterized by its interlocking grain and attractive reddish-brown hue. It is known for its strength, durability, and resistance to splitting, making it a popular choice for furniture, flooring, and decorative woodworking projects.'),
(1830, 177, '_services_details_repeater_3_service_details_content', 'field_64f6e4f329d8b'),
